sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading) {
      return;
   }

   //Type appropriate comment here, and begin script below
	var ga = new GlideAjax('sn_kpi_composer.LinkedIndicatorDetails');
	ga.addParam('sysparm_name','getIndicatorConditions');
	ga.addParam('sysparm_ind',g_form.getValue('pa_indicator'));
	ga.getXMLAnswer(populateConditions);
}

function populateConditions(answer) {
	g_form.setValue('indicator_details',answer);
}
