sys_script_include.script:
var ProjectValidationIndicatorDefinitionNotLinkedInCanvas = Class.create();
ProjectValidationIndicatorDefinitionNotLinkedInCanvas.prototype = Object.extendsObject(sn_kpi_composer.ProjectValidation, {

	validate: function(data) {
		var that = this;
		if (this.trace) this._debug.push("ProjectValidationIndicatorDefinitionNotLinkedInCanvas");

        // For all entities in the project, check if a entity has a related indicator defintion, if so
        // the entity should be used in a dashboard of a canvas with a pa visualisation 
        data.entities.forEach(function(entity) {
            // check if the entity has a indicator definition
            if (entity.indicator_definition !== null) {

                var hasCanvasWithPaVisualisation = false;
                // Scan all canvasses, at lease one (with this entity related) 
                that._apply_on_dashboards(data.dashboards, 
                    function(dashboard) { 
                        that._apply_on_tabs(dashboard.tabs,										
                            function(tab) {		
                                that._apply_on_rows(tab.rows, 
                                    function(row) {
                                        that._apply_on_canvasses(row.canvasses, function(canvas) {

                                            // If this canvas is a report, continue with the check
                                            if (canvas.widget_type == "Performance Analytics") {

                                                // Take all entities (sys_id's) 
                                                var canvas_entities = that._getDistinctArrayFromCSS(canvas.entities);

                                                // check if this is the entity we are checking, then it's ok!
                                                canvas_entities.forEach(function(entity_sys_id) { 
                                                    if (entity_sys_id == entity.sys_id) 
                                                    hasCanvasWithPaVisualisation = true;
                                                });
                                            }
                                        });
                                });
                            }
                        );
                    if (!hasCanvasWithPaVisualisation) {
                        that._addProjectValidationInfo(
                            { 
                                description: gs.getMessage("Artifact '{0}' has a indicator definition, but there is no performance analytics visualisation on any dashboard.", [entity.name, entity.report_definition]),
                                table: "sn_kpi_composer_entity",
                                record: entity.sys_id,
                            }, { project: { sys_id: data.project.sys_id } });											
                    }									

                });
            }
        });
    },

    type: 'ProjectValidationIndicatorDefinitionNotLinkedInCanvas'
});
