sys_script_include.script:
var ProjectValidationDashboardWithoutWidgets = Class.create();
ProjectValidationDashboardWithoutWidgets.prototype = Object.extendsObject(sn_kpi_composer.ProjectValidation, {

	validate: function(data) {

		var that = this;

		if (this.trace) this._debug.push("ProjectValidationDashboardWithoutWidgets");	

		// this._debug.push("ProjectValidationDashboardWithoutWidgets");
		// this._debug.push(JSON.stringify(data));	

		for(var idx = 0; idx<data.dashboards.length; idx++) {

			var dashboard = data.dashboards[idx];
			var hasvisualization = false;

			dashboard.tabs.forEach(function(tab) {
				tab.rows.forEach(function(row) {
					row.canvasses.forEach(function(canvas) {
						if (canvas.widget_type)
							hasvisualization = true;
					});
				});
			});


			if (hasvisualization == false) {

				var validation = {};
				validation.description = gs.getMessage("Dashboard has no widgets");
				validation.table = "sn_kpi_composer_dashboard";
				validation.record = dashboard.sys_id;

				this._addProjectValidationDefinitionError(validation, { project: { sys_id: data.project.sys_id } });

			}		

		};

	},

    type: 'ProjectValidationDashboardWithoutWidgets'
});
