function globalFunctions() {
	// Leave this function so this code will load
}

// Array with objects containing sys_id property
function findBySysId(arr, sys_id) {	
	try {
		for(var i = 0, m = arr.length; i<m; i++) {
			var e = arr[i];
			if (e.sys_id == sys_id)
				return e;
			// If not found, check if it is a library entity
			else if (e.org_sys_id && e.org_sys_id == sys_id)
				return e;
		}	
	} catch(err) {
		gs.error(err);
	}
	return null;
}
// Instead of checking the objects sys_id, the array is a list of sys_id's itself
function findBySysIdList(arr, sys_id) {	
	try {
		for(var i = 0, m = arr.length; i<m; i++) {
			var e = arr[i];
			if (e == sys_id)
				return e;
		}	
	} catch(err) {
		gs.error(err);
	}
	return null;
}
