sys_script_include.script:
var BusinessRulesFunctions = Class.create();
BusinessRulesFunctions.prototype = {
    initialize: function() {
    },

	/*
	* For every field in the record, check if there is a attribute defined called 'track_definition_updated'
	* When the value is set to true, and the value in the field (of the record) is changed
	* set the [definition_updated] value to this moment and add then name of the user in the [definition_updated_by]
	* This makes it possible for end users to define on what fields the [definition_updated] is modified and checked
	*/
	checkDefinitionUpdateByAttribute: function(current, previous) {
		try {
			for(var field in current) {
				if (field) {
					var glidObj = current[field];
					if (glidObj != null) {
						var atr = glidObj.getAttribute("track_definition_updated");
						if (atr && atr == "true") {
							if (current[field] != previous[field]) {
								current.definition_updated = new GlideDateTime();
								current.definition_updated_by = gs.getUserName();
							}
						}		
					}
				}
			}
		} catch(e) {
			gs.error(JSON.stringify(e));
		}
	},	

    type: 'BusinessRulesFunctions'
};
