sys_script_include.script:
function projectEntities(input) {


	var sysids = [];

	if (!input || !input.project || !input.project.sys_id) {
		input = {};
		input.project = {};
		input.project.sys_id = "NO_PROJECT_SYS_ID";
	} 

	try {

		// Api to entity
		var entityApi = new sn_kpi_composer.EntityApi();	 

		// Get distinct data
		var data = entityApi.getDistinctData(input);

		// Take all entities and take there sys_id
		data.entities.forEach(function(e) {
			sysids.push(e.sys_id);
		});
	}
	catch(ex) {
		sysids.push(ex.message);
	}

	return sysids;
}
