sys_script_include.script:
var ProjectValidationFormulaIndWithoutSupInd = Class.create();
ProjectValidationFormulaIndWithoutSupInd.prototype = Object.extendsObject(sn_kpi_composer.ProjectValidation, {

	validate: function(data) {
		var that = this;
		if (this.trace) this._debug.push("ProjectValidationFormulaIndWithoutSupInd");	

		var inddefs = data.indicatordefinitions;						
		for(var idx = 0; idx<inddefs.length; idx++) {
			// Take the indicator
			var inddef = data.indicatordefinitions[idx];

			// Formula indicator
			if (inddef.indicator_type == this._indicatorDefinitionTypeFormula) {								
				/*
				* > Formula Indicator Definition zonder supporting Indicators
				*/
				var hasSubIndicators = false;
				for(var ddx = 0; ddx<data.indicatordefinition_indicatordefinition.length; ddx++) {
					var subInd = data.indicatordefinition_indicatordefinition[ddx];
					if (subInd.main_indicator_definition == inddef.sys_id) {
						hasSubIndicators = true;
					}
				}

				if (hasSubIndicators == false) {
					this._addProjectValidationWarning(
						{ 
							description: gs.getMessage("Formula Indicator definition has no supporting indicators"),
							table: "sn_kpi_composer_indicator_definition",
							record: inddef.sys_id,
						}, { project: { sys_id: data.project.sys_id } });
				}
			}
		}		

	},

    type: 'ProjectValidationFormulaIndWithoutSupInd'
});
