sys_script_include.script:
var ProjectValidationWidgetWithoutMeasurement = Class.create();
ProjectValidationWidgetWithoutMeasurement.prototype = Object.extendsObject(sn_kpi_composer.ProjectValidation, {

	validate: function(data) {
		var that = this;
		if (this.trace) this._debug.push("ProjectValidationWidgetWithoutMeasurement");

		this._apply_on_dashboards(data.dashboards, 
			function(dashboard) { 
				that._apply_on_tabs(dashboard.tabs,										
					function(tab) {		
						that._apply_on_rows(tab.rows, 
							function(row) {
								that._apply_on_canvasses(row.canvasses, function(canvas) {
									// Check if a canvas (widget) has one or more entities (measurements/cf/?)
									var canvas_entities = that._getDistinctArrayFromCSS(canvas.entities);
									// ----------------------------------------------
									// Widget definition has no related measurements
									// ----------------------------------------------
									if (canvas_entities.length == 0 && canvas.widgetvisualisation) {
										that._addProjectValidationDefinitionError(
											{ 
												description: gs.getMessage("Widget definition '{0}' on dashboard/tab:'{1}.{2}' has no related measurements", [canvas.title, dashboard.name, tab.caption]),
												table: "sn_kpi_composer_canvas",
												record: canvas.sys_id,
											}, { project: { sys_id: data.project.sys_id } });											
									}									
								});
						});
					}
				);
		});
	},

    type: 'ProjectValidationWidgetWithoutMeasurement'
});
