sys_script_include.script:
var ProjectValidationMeasurementFollowElementButMissingBreakdown = Class.create();
ProjectValidationMeasurementFollowElementButMissingBreakdown.prototype = Object.extendsObject(sn_kpi_composer.ProjectValidation, {

	validate: function(data) {
		var that = this;
		if (this.trace) this._debug.push("ProjectValidationMeasurementFollowElementButMissingBreakdown");

		var globals_x = sn_kpi_composer.globalFunctions();

		this._apply_on_dashboards(data.dashboards, 
			function(dashboard) { 
				// Get breakdowns from the dashboard
				var breakdowns_dashboard = that._getObjectsFromArrayBySysId(data.breakdowns, that._getDistinctArrayFromCSS(dashboard.breakdowns));

				//that._debug.push("Dashboard: '" + dashboard.name + "' has breakdowns: " + JSON.stringify(breakdowns_dashboard));

				// Only check if the dashboard has breakdowns
				if (breakdowns_dashboard.length > 0) {

					that._apply_on_tabs(dashboard.tabs,										
										function(tab) {		
						that._apply_on_rows(tab.rows, 
											function(row) {
							that._apply_on_canvasses(row.canvasses, function(canvas) {

								// Check if follow element is set
								if (canvas.follow_element) {	
									// Take entities from canvas
									// that._debug.push("Canvas: " + JSON.stringify(canvas));																		
									var entities = that._getObjectsFromArrayBySysId(data.entities, that._getDistinctArrayFromCSS(canvas.entities));

									//that._debug.push("Check canvas (FE) entities: " + JSON.stringify(entities));
									//that._debug.push("Data.entities: " + JSON.stringify(data.entities));
									//that._debug.push("Canvas.entities: " + JSON.stringify(that._getDistinctArrayFromCSS(canvas.entities)));

									// Check all elements, all should follow breakdowns
									for(var ei = 0, em = entities.length; ei<em; ei++) {

										var entity = entities[ei];

										for(var bi = 0, bm = breakdowns_dashboard.length; bi<bm; bi++) {
											var breakdown = breakdowns_dashboard[bi];

											if (that.trace) that._debug.push("entity (canvas):" + JSON.stringify(entity));
											if (that.trace) that._debug.push("breakdown (dashboard):" + JSON.stringify(breakdown));

											// Does the entity contain the breakdown?
											if (sn_kpi_composer.findBySysIdList(entity.breakdowns, breakdown.sys_id) == null) {
												// If not log a error
												that._addProjectValidationDefinitionError(
													{ 
														description:  gs.getMessage("Measurement '{0}' is missing breakdown '{1}' to follow element on dashboard '{2}'", [entity.name, breakdown.name, dashboard.name]),
														table: "sn_kpi_composer_canvas",
														record: canvas.sys_id,
													}, { project: { sys_id: data.project.sys_id } });
											}
										}
									}									
								}
							});
						});
					});
				}
			});
	},


    type: 'ProjectValidationMeasurementFollowElementButMissingBreakdown'
});
