sys_script_include.script:
var ProjectValidationNoRowOnTab = Class.create();
ProjectValidationNoRowOnTab.prototype = Object.extendsObject(sn_kpi_composer.ProjectValidation, {

	validate: function(data) {
		var that = this;
		if (this.trace) this._debug.push("ProjectValidationNoRowOnTab");

		this._apply_on_dashboards(data.dashboards, 
				function(dashboard) { 
					that._apply_on_tabs(dashboard.tabs,										
						function(tab) {
							//--------------------
							// Tab "#T_NAME#" on dashboard "#D_NAME#" has no Rows defined
							//--------------------
							if (tab.rows.length == 0) {
								that._addProjectValidationWarning(
									{ 
										description: gs.getMessage("Tab '{0}' on dashboard '{1}' has no Rows defined", [tab.caption, dashboard.name]),
										table: "sn_kpi_composer_tab",
										record: tab.sys_id,
									}, { project: { sys_id: data.project.sys_id } });	
							}
						}
					);
				}
			);
	},

    type: 'ProjectValidationNoRowOnTab'
});
