sys_script_include.script:
var ProjectValidationNoTabOnDashboard = Class.create();
ProjectValidationNoTabOnDashboard.prototype = Object.extendsObject(sn_kpi_composer.ProjectValidation, {

	validate: function(data) {
		var that = this;
		if (this.trace) this._debug.push("ProjectValidationNoTabOnDashboard");

		this._apply_on_dashboards(data.dashboards, 
				function(dashboard) { 	
					// -----------
					// Dashboard "#D_NAME#" has no Tabs defined
					// -----------
					if (dashboard.tabs.length == 0) {								
						that._addProjectValidationWarning(
							{ 																		
								description:  gs.getMessage("Dashboard '{0}' has no Tabs defined", dashboard.name),
								table: "sn_kpi_composer_dashboard",
								record: dashboard.sys_id,
							}, { project: { sys_id: data.project.sys_id } });								
					}
				});
	},

    type: 'ProjectValidationNoTabOnDashboard'
});
