sys_script_include.script:
var ProjectValidationAutomatedIndicatorDefNoFactTable = Class.create();
ProjectValidationAutomatedIndicatorDefNoFactTable.prototype = Object.extendsObject(sn_kpi_composer.ProjectValidation, {

	validate: function(data) {
		var that = this;
		if (this.trace) this._debug.push("ProjectValidationAutomatedIndicatorDefNoFactTable");	

		var inddefs = data.indicatordefinitions;						
		for(var idx = 0; idx<inddefs.length; idx++) {
			// Take the indicator
			var inddef = data.indicatordefinitions[idx];

			// Automated indicator  (don't use === its a const)
			if (inddef.indicator_type == this._indicatorDefinitionTypeAutomated) {
				/*
				* > Automated Indicator Definition zonder Facts table
				*/								
				if (inddef.facts_table === null && inddef.pa_indicator == null) {
					this._addProjectValidationDefinitionError(
						{ 
							description: gs.getMessage("Indicator definition has no facts table"),
							table: "sn_kpi_composer_indicator_definition",
							record: inddef.sys_id,
						}, { project: { sys_id: data.project.sys_id } });
				}
			}		
		}

	},

    type: 'ProjectValidationAutomatedIndicatorDefNoFactTable'
});
