sys_script_include.script:
var ProjectValidationMeasureHasMultipleDefinitions = Class.create();
ProjectValidationMeasureHasMultipleDefinitions.prototype = Object.extendsObject(sn_kpi_composer.ProjectValidation, {

	validate: function(data) {
		var that = this;
		if (this.trace) this._debug.push("ProjectValidationMeasureHasMultipleDefinitions");

        data.entities.forEach(function(e) {
            if (e.indicator_definition && e.report_definition) {
                that._addProjectValidationWarning(
                    { 
                        description: gs.getMessage("Artifact '{0}' has a report definition and indicator definition.", [e.name]),
                        table: "sn_kpi_composer_entity",
                        record: e.sys_id,
                    }, { project: { sys_id: data.project.sys_id } });	
            }            
        });
	},
    type: 'ProjectValidationMeasureHasMultipleDefinitions'
});
