sys_script_include.script:
var ProjectValidationReportDefHasNoFactTable = Class.create();
ProjectValidationReportDefHasNoFactTable.prototype = Object.extendsObject(sn_kpi_composer.ProjectValidation, {

	validate: function(data) {
		var that = this;
		if (this.trace) this._debug.push("ProjectValidationReportDefHasNoFactTable");	

		// Loop all entities
		for(var idx = 0; idx<data.entities.length; idx++) {
			var entity = data.entities[idx];

			// Check the Measurement
			if (entity.entitytype == this._entityTypeMeasurement && entity.report_definition != null) {
				/*
				* > measure of type report definition should have a fact table
				*/
				// Find the report definition
				data.report_definitions.forEach(function(rep_def) {
					if (rep_def.sys_id === entity.report_definition) {
						if (rep_def.facts_table == null) {
							that._addProjectValidationDefinitionError(
								{ 
									description: gs.getMessage("Measurement with reportdefinition has no fact table."),
									//table: "sn_kpi_composer_report_definition",
									table: "sn_kpi_composer_entity",									
									record: entity.sys_id,
								}, { project: { sys_id: data.project.sys_id } });										
						}
					}
				});
			}
		}				
	},


    type: 'ProjectValidationReportDefHasNoFactTable'
});
