sys_script_include.script:
var ProjectTaskCreateSpotlight = Class.create();
ProjectTaskCreateSpotlight.prototype = Object.extendsObject(sn_kpi_composer.ProjectTask, {

	createtasks: function(data) {
		var that = this;
		if (this.trace) this._debug.push("ProjectTaskCreateSpotlight");	

		var globals_x = sn_kpi_composer.globalFunctions();

		data.dashboards.forEach(function(d) {
			d.tabs.forEach(function(t) {
				t.rows.forEach(function(r) {
					r.canvasses.forEach(function(canvas) {
						//--
						if (canvas.widget_type == "Spotlights") {							

							var defRecId = canvas.sys_id;
							var defTableNm = 'sn_kpi_composer_canvas';

							var taskDefRec = that._addOrGetTaskDefinitionRecord(defTableNm, defRecId, data.project.sys_id);
							if (that.trace) that._debug.push("Lookup DefRecId 8: " + defRecId + " for DefRecTableNm: " + defTableNm + " => " + JSON.stringify(taskDefRec));

							// Generic update of the status
							that._updateTaskDefinitionStatus(taskDefRec, canvas, canvas.spotlight_group, data);

							// Set artifact source table
							taskDefRec.artifact_table = 'spotlight_groups';

							// Set the artifact target record
							taskDefRec.artifact_record = canvas.spotlight_group;

							var desc =  gs.getMessage("{0}Spotlight name '{1}'", [taskDefRec.validationerrors, canvas.title]);
							if (canvas.description) desc +=  gs.getMessage("\nDescription: '{0}'", canvas.description);

							if (canvas.entities) {
								desc += "\nBased on artifact(s): ";
								var e_cnt = 0;
								canvas.entities.split(',').forEach(function(e_sys_id) {
									var entity = sn_kpi_composer.findBySysId(data.entities, e_sys_id);
									if (entity) {
										if (e_cnt>0) desc += ", ";
										desc += entity.name;
										e_cnt++;
									}
								});
							}

							taskDefRec.description = desc;

							var result = that._updateTaskDefinitionRecord(taskDefRec);

						}
					});
				});
			});
        });
	},


    type: 'ProjectTaskCreateSpotlight'
});
