sys_script_include.script:
var ProjectTaskCreateContentBlock = Class.create();
ProjectTaskCreateContentBlock.prototype = Object.extendsObject(sn_kpi_composer.ProjectTask, {

	createtasks: function(data) {
		var that = this;
		if (this.trace) this._debug.push("ProjectTaskCreateContentBlock");	

		data.dashboards.forEach(function(d) {

			d.tabs.forEach(function(t) {
				t.rows.forEach(function(r) {
					r.canvasses.forEach(function(canvas) {
						// Is this a content block?

						if (canvas.widget_type == "Content Blocks") {

							var defRecId = canvas.sys_id;
							var defTableNm = 'sn_kpi_composer_canvas';

							var taskDefRec = that._addOrGetTaskDefinitionRecord(defTableNm, defRecId, data.project.sys_id);
							if (that.trace) that._debug.push("Lookup DefRecId 2: " + defRecId + " for DefRecTableNm: " + defTableNm + " => " + JSON.stringify(taskDefRec));						

							// Generic update of the status
							that._updateTaskDefinitionStatus(taskDefRec, canvas, canvas.content_block, data);

							// Set artifact source table
							taskDefRec.artifact_table = 'content_block';

							// Set the artifact target record
							taskDefRec.artifact_record = canvas.content_block;

							// what is the content block to create?
							var desc =  gs.getMessage("{0}Content block name: '{1}'", [taskDefRec.validationerrors, canvas.title]);
							if (canvas.description) desc +=  gs.getMessage("\nDescription: '{0}'", canvas.description);

							taskDefRec.description = desc;

							var result = that._updateTaskDefinitionRecord(taskDefRec);

						}

					});					
				});
			});
		});
	},

    type: 'ProjectTaskCreateContentBlock'
});
