sys_script_include.script:
var ProjectValidationFollowElementWidgetDashboardNoBreakdowns = Class.create();
ProjectValidationFollowElementWidgetDashboardNoBreakdowns.prototype = Object.extendsObject(sn_kpi_composer.ProjectValidation, {

	validate: function(data) {
		var that = this;
		if (this.trace) this._debug.push("ProjectValidationFollowElementWidgetDashboardNoBreakdowns");

		this._apply_on_dashboards(data.dashboards, 
			function(dashboard) { 
				// Get breakdowns from the dashboard
				var breakdowns = that._getObjectsFromArrayBySysId(data.breakdowns, that._getDistinctArrayFromCSS(dashboard.breakdowns));

				// that._debug.push("Dashboard: '" + dashboard.name + "' has breakdowns: " + JSON.stringify(breakdowns));

				// Only check if the dashboard has breakdowns
				if (breakdowns.length == 0) {

					that._apply_on_tabs(dashboard.tabs,										
										function(tab) {		
						that._apply_on_rows(tab.rows, 
											function(row) {
							that._apply_on_canvasses(row.canvasses, function(canvas) {

								// Check if follow element is set
								if (canvas.follow_element) {	

									that._addProjectValidationWarning(
										{ 																		
											description:  gs.getMessage("Follow Element enabled for widget definiton '{0}' while dashboard '{1}' has no breakdowns", [canvas.name, dashboard.name]),
											table: "sn_kpi_composer_canvas",
											record: canvas.sys_id,
										}, { project: { sys_id: data.project.sys_id } });										

								}

							});
						});
					});
				}
		});
	},

    type: 'ProjectValidationFollowElementWidgetDashboardNoBreakdowns'
});
