sys_script_include.script:
var ProjectValidationBussinessGoalFacNoIndDef = Class.create();
ProjectValidationBussinessGoalFacNoIndDef.prototype = Object.extendsObject(sn_kpi_composer.ProjectValidation, {

	validate: function(data) {
		var that = this;
		if (this.trace) this._debug.push("ProjectValidationBussinessGoalFacNoIndDef");	


		// Loop all entities
		for(var idx = 0; idx<data.entities.length; idx++) {
			var entity = data.entities[idx];

			// Check the Bussiness Goal
			if (entity.entitytype == this._entityTypeBusinessGoal) {
				/*
				* Critical Success Factor zonder Indicator definition"
				*/
				if (entity.indicator_definition == null) {									
					if (entity.used_in_canvas && entity.used_in_canvas.length>0) {
						this._addProjectValidationDefinitionError(
							{ 
								description: gs.getMessage("Bussiness Goal has no indicator definition"),
								table: "sn_kpi_composer_entity",
								record: entity.sys_id,
							}, { project: { sys_id: data.project.sys_id } });									

					} else {
						this._addProjectValidationWarning(
							{ 
								description: gs.getMessage("Bussiness Goal has no indicator definition"),
								table: "sn_kpi_composer_entity",
								record: entity.sys_id,
							}, { project: { sys_id: data.project.sys_id } });									
					}
				}								
			}				
		}		
	},

    type: 'ProjectValidationBussinessGoalFacNoIndDef'
});
