sys_script_include.script:
function projectBreakdowns (input) { 

	if (!input || !input.project || !input.project.sys_id) {
		input = {};
		input.project = {};
		input.project.sys_id = "NO_PROJECT_SYS_ID";
	} 	 

	var entityApi = new sn_kpi_composer.EntityApi();	 
	var data = entityApi.getData(input);	

	// All availible breakdowns
	var breakdowns = [];
	for(var idx = 0; idx<data.entities.length; idx++) {
		var entity = data.entities[idx];
		for(var ibd = 0; ibd<entity.breakdowns.length; ibd++) {
			if (breakdowns.indexOf(entity.breakdowns[ibd])<0) {
				breakdowns.push(entity.breakdowns[ibd]);
			}
		}
	}
    return breakdowns;
}
