sys_script_include.script:
var ProjectValidationEntityNotUsedAnyDashboard = Class.create();
ProjectValidationEntityNotUsedAnyDashboard.prototype = Object.extendsObject(sn_kpi_composer.ProjectValidation, {

	validate: function(data) {
		var that = this;
		if (this.trace) this._debug.push("ProjectValidationEntityNotUsedAnyDashboard");

		var all_entity_sys_ids = [];

		this._apply_on_dashboards(data.dashboards, 
			function(dashboard) { 
				// All entities on this dashboards
				var entity_sys_ids = that._all_used_entities_in_dashboard(dashboard);
				all_entity_sys_ids = all_entity_sys_ids.concat(entity_sys_ids);
			});		

		// We got all used entities now: all_used_entities
		// Check if there are any entities that are not set in any dashboard
		for(var ei =0, em = data.entities.length; ei<em; ei++) {
			var entity = data.entities[ei];
			// Check if this entity is used
			if (all_entity_sys_ids.filter(function(e) { return e == entity.sys_id }).length == 0) {
				// Not used!								
				//----------------
				// Measurement "#M_NAME#" is not included in any dashboard
				//----------------
				that._addProjectValidationWarning(
					{ 																		
						description:  gs.getMessage("Entity '{0}' is not included in any dashboard", entity.name),
						table: "sn_kpi_composer_entity",
						record: entity.sys_id,
					}, { project: { sys_id: data.project.sys_id } });									
			}
		}		

	},

    type: 'ProjectValidationEntityNotUsedAnyDashboard'
});
