sys_script_include.script:
var ProjectTaskCreateInteractiveFilter = Class.create();
ProjectTaskCreateInteractiveFilter.prototype = Object.extendsObject(sn_kpi_composer.ProjectTask, {

	createtasks: function(data) {
		var that = this;
		if (this.trace) this._debug.push("ProjectTaskCreateInteractiveFilter");	

		data.dashboards.forEach(function(d) {
			d.tabs.forEach(function(t) {
				t.rows.forEach(function(r) {
					r.canvasses.forEach(function(canvas) {
						if (canvas.widget_type == "Interactive Filters") {

							if (that.trace) that._debug.push("Create/update interactive filter task: " + canvas.title);
							var defRecId = canvas.sys_id;
							var defTableNm = 'sn_kpi_composer_canvas';

							// Lookup or create record
							var taskDefRec = that._addOrGetTaskDefinitionRecord(defTableNm, defRecId, data.project.sys_id);
							if (that.trace) that._debug.push("Lookup DefRecId 5: " + defRecId + " for DefRecTableNm: " + defTableNm + " => " + JSON.stringify(taskDefRec));

							// Generic update of the status
							that._updateTaskDefinitionStatus(taskDefRec, canvas, canvas.interactive_filter, data);

							// Set artifact source table
							taskDefRec.artifact_table = 'sys_ui_hp_publisher';

							// Set the artifact target record
							taskDefRec.artifact_record = canvas.interactive_filter;

							var desc = gs.getMessage("{0}Interactive filter name'{1}'", [taskDefRec.validationerrors, canvas.title]);
							desc +=  gs.getMessage("\nUI Control type: '{0}'", canvas.widgetvisualisation_dv);
							if (canvas.description) desc +=  gs.getMessage("\nDescription: '{0}'", canvas.description);

							taskDefRec.description = desc;

							var result = that._updateTaskDefinitionRecord(taskDefRec);
						}
					});
				});
			});
        });
	},

    type: 'ProjectTaskCreateInteractiveFilter'
});
