sys_script_include.script:
var ProjectValidationMeasureWithBreakdownsOnlyReport = Class.create();
ProjectValidationMeasureWithBreakdownsOnlyReport.prototype = Object.extendsObject(sn_kpi_composer.ProjectValidation, {

	validate: function(data) {
		var that = this;
		if (this.trace) this._debug.push("ProjectValidationMeasureWithBreakdownsOnlyReport");

        // Loop all entities
        data.entities.forEach(function(e) {

            // Check if the entity has breakdown AND is used in any canvas
            if (e.breakdowns.length > 0 && e.used_in_canvas.length > 0) {
                // Loop all canvassess
                // Check if the all the canvas types are report
                var usedInReportOnly = true;
                e.used_in_canvas.forEach(function(c) {
                    if (c.widget_type != "Reports")
                    usedInReportOnly = false;
                });
                if (usedInReportOnly) {
                    that._addProjectValidationWarning(
                        { 
                            description: gs.getMessage("Artifact '{0}' ({1}) has breakdowns but is only used in a report.", [e.name, e.entitytype_dv]),
                            table: "sn_kpi_composer_entity",
                            record: e.sys_id,
                        }, { project: { sys_id: data.project.sys_id } });	
                    }
                }            
        });
	},
    type: 'ProjectValidationMeasureWithBreakdownsOnlyReport'
});
