sys_script_include.script:
var ProjectValidationBreakdownDefWithoutFactsTable = Class.create();
ProjectValidationBreakdownDefWithoutFactsTable.prototype = Object.extendsObject(sn_kpi_composer.ProjectValidation, {

	validate: function(data) {
		var that = this;
		if (this.trace) this._debug.push("ProjectValidationBreakdownDefWithoutFactsTable");	

		/*
		* Breakdowns, check only when used
		*/
		var breakdowns = this._projectApi.breakdownsInProject(data);
		for(var bdx = 0; bdx<breakdowns.length; bdx++) {
			var breakdown = breakdowns[bdx];
			/* 
			* > Breakdown Definition zonder Facts table
			*/
			if (breakdown.facts_table == null && breakdown.pa_breakdown == null) {
				this._addProjectValidationDefinitionError(
					{ 
						description: gs.getMessage("Breakdown definition has no facts table"),
						table: "sn_kpi_composer_breakdown",
						record: breakdown.sys_id,
					}, { project: { sys_id: data.project.sys_id } });
			}																
		}		

	},
    type: 'ProjectValidationBreakdownDefWithoutFactsTable'
});
