sys_script_include.script:
var ProjectValidationMeasurementHasNoIndDef = Class.create();
ProjectValidationMeasurementHasNoIndDef.prototype = Object.extendsObject(sn_kpi_composer.ProjectValidation, {

	validate: function(data) {
		var that = this;
		if (this.trace) this._debug.push("ProjectValidationMeasurementHasNoIndDef");	

		// Loop all entities
		for(var idx = 0; idx<data.entities.length; idx++) {
			var entity = data.entities[idx];

			// Check the Measurement
			if (entity.entitytype == this._entityTypeMeasurement) {
				/*
				* > measure zonder Indicator definition
				* > should not have a report definition
				*/
				if (entity.indicator_definition == null && entity.report_definition == null) {
					this._addProjectValidationDefinitionError(
						{ 
							description: gs.getMessage("Measurement has no indicator definition or report definition."),
							table: "sn_kpi_composer_entity",
							record: entity.sys_id,
						}, { project: { sys_id: data.project.sys_id } });									
				}
			}

		}		

	},

    type: 'ProjectValidationMeasurementHasNoIndDef'
});
