sys_script_include.script:
var LinkedIndicatorDetails = Class.create();
LinkedIndicatorDetails.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getIndicatorConditions: function() {

		var ind = this.getParameter('sysparm_ind');

		if (!ind)
			return '';

		// Using GlideRecordSecure prevents the details from loading, even when having correct PA roles (not being sys admin). 
		// Script is not executed at all if user has no access to pa_indicator table.
		var gr_ind = new GlideRecord('pa_indicators');
		gr_ind.get(ind);

		if(!gr_ind.canRead()){
			gs.warn("No read access to given indicator");
			return '';
		}
		var summary = gr_ind.type;

		// Check if automated indicator
		if (gr_ind.type == 1){

			var ind_facts_table = gr_ind.cube.facts_table.getDisplayValue();
			var ind_conditions = gr_ind.conditions.getDisplayValue();
			var ind_source_conditions = gr_ind.cube.conditions.getDisplayValue();

			if (!ind_conditions)
				ind_conditions = "None";

			if (!ind_source_conditions)
				ind_conditions = "None";

			summary = 'Facts table: ' + ind_facts_table + '\nIndicator source conditions: ' + ind_source_conditions + '\nAdditional Indicator conditions: ' + ind_conditions;
		}

		// Check if formula indicator
		if (gr_ind.type == 2){

			// Convert sysid references in formula to display values
			var formulaTranslated = new global.ScopedPAFormula(gr_ind.getValue('formula'), gr_ind.getValue('sys_id'));

			if (formulaTranslated.isValid()){
				summary = 'Formula: \n' + formulaTranslated.getDisplay();
			}
		}

		return summary;
	},

	isPublic: function() {
		return false;
	},

    type: 'LinkedIndicatorDetails'
});
