sys_script_include.script:
var ProjectValidationMeasureUsedInBothTypes = Class.create();
ProjectValidationMeasureUsedInBothTypes.prototype = Object.extendsObject(sn_kpi_composer.ProjectValidation, {

	validate: function(data) {
		var that = this;
		if (this.trace) this._debug.push("ProjectValidationMeasureUsedInBothTypes");

        // Loop all entities
        data.entities.forEach(function(e) {
            // Loop all canvassess
            var usedInReport = false;
            var usedInPaIndicator = false;
            if (e.used_in_canvas) {
                e.used_in_canvas.forEach(function(c) {
                    if (c.widget_type == "Performance Analytics")
                        usedInPaIndicator = true;
                    if (c.widget_type == "Reports")
                        usedInReport = true;
                });
                if (usedInPaIndicator && usedInReport) {
                    that._addProjectValidationInfo(
                        { 
                            description: gs.getMessage("Artifact '{0}' ({1}) is used in a report and performance analytics widgets.", [e.name, e.entitytype_dv]),
                            table: "sn_kpi_composer_entity",
                            record: e.sys_id,
                        }, { project: { sys_id: data.project.sys_id } });	
                }            
            }
        });
	},
    type: 'ProjectValidationMeasureUsedInBothTypes'
});
