sys_script_include.script:
var ProjectTaskValidateTaskDefOrphans = Class.create();
ProjectTaskValidateTaskDefOrphans.prototype = Object.extendsObject(sn_kpi_composer.ProjectTask, {

    /*

        Remove all task definition record that have empty projects list reference

    */


	validatetasks: function(data) {
		var that = this;

		if (this.trace) this._debug.push("ProjectTaskValidateTaskDefOrphans, Active project: " + data.project.sys_id + " (" + data.project.name + ")");

        // Take all task-def record for the active project
        var gr_taskDef = new GlideRecord('sn_kpi_composer_task_definition');
        gr_taskDef.addQuery("projects", "");
        gr_taskDef.addQuery("status", '!=', 5);
        gr_taskDef.query();

        while(gr_taskDef.next()) {
            if (this.trace) this._debug.push("Found taskdef " + gr_taskDef.getValue("sys_id") + " with no projects, setting status to 5");
            gr_taskDef.setValue("status", "5");
            gr_taskDef.update();
        }

	},
    type: 'ProjectTaskValidateTaskDefOrphans'
});
