sys_script_include.script:
var ProjectTaskCheckBreakdown = Class.create();
ProjectTaskCheckBreakdown.prototype = Object.extendsObject(sn_kpi_composer.ProjectTask, {

	createtasks: function(data) {
		var that = this;
		if (this.trace) this._debug.push("ProjectTaskCheckBreakdown");

		// Select all breakdowns from a project
		var breakdowns = this._projectApi.breakdownsInProject(data);
		for(var bdx = 0; bdx<breakdowns.length; bdx++) {
			var breakdown = breakdowns[bdx];		

			// Is there a pa_breakdown linked?
			if (breakdown.pa_breakdown != null) {

			var desc = "";

				var checkBreakdownTask = {};
				checkBreakdownTask.type = this._projectTaskValidateBreakdown;
				checkBreakdownTask.pa_record = breakdown.pa_breakdown;
				checkBreakdownTask.pa_table = 'pa_breakdowns';						
				checkBreakdownTask.title = gs.getMessage("Validate breakdown '{0}'", breakdown.pa_breakdown_dv);

				desc += gs.getMessage("Validate breakdown '{0}'", breakdown.pa_breakdown_dv);
				desc += gs.getMessage("\nCheck if the following properties are set:");
				desc += gs.getMessage("\nFacts table:{0}", breakdown.facts_table);
				if (breakdown.conditions)
					desc += gs.getMessage("\nCondition:{0}", breakdown.conditions);

				checkBreakdownTask.table = 'sn_kpi_composer_breakdown';
				checkBreakdownTask.record = breakdown.sys_id;		

				checkBreakdownTask.description = desc;
				this._addProjectTask(checkBreakdownTask, data.input);


			}
		}

	},

    type: 'ProjectTaskCheckBreakdown'
});
