sys_script_include.script:
var ProjectValidationIndicatorDefNotLinkedPaIndicator = Class.create();
ProjectValidationIndicatorDefNotLinkedPaIndicator.prototype = Object.extendsObject(sn_kpi_composer.ProjectValidation, {

	validate: function(data) {
		var that = this;
		if (this.trace) this._debug.push("ProjectValidationIndicatorDefNotLinkedPaIndicator");

        data.indicatordefinitions.forEach(function(ind_def) {

            if (ind_def.pa_indicator == null) {
                that._addProjectValidationWarning(
                    { 
                        description: gs.getMessage("Indicator definition '{0}' has no linked pa indicator.", [ind_def.name]),
                        table: "sn_kpi_composer_indicator_definition",
                        record: ind_def.sys_id,
                    }, { project: { sys_id: data.project.sys_id } });
                };
        });
	},

    type: 'ProjectValidationIndicatorDefNotLinkedPaIndicator'
});
