sys_script_include.script:
var ProjectValidationEntityUsedMultipleTimesOnDashboard = Class.create();
ProjectValidationEntityUsedMultipleTimesOnDashboard.prototype = Object.extendsObject(sn_kpi_composer.ProjectValidation, {

	validate: function(data) {
		var that = this;
		if (this.trace) this._debug.push("ProjectValidationEntityUsedMultipleTimesOnDashboard");

		this._apply_on_dashboards(data.dashboards, 
			function(dashboard) { 
				// All entities on this dashboards
				var entity_sys_ids = that._all_used_entities_in_dashboard(dashboard);


				//---------------------------
				// Duplicate widget / measurement combination found on dashboard "#D_NAME#"
				//---------------------------
				for(var ei =0, em = data.entities.length; ei<em; ei++) {
					var entity = data.entities[ei];


					// Check if this entity is used
					if (entity_sys_ids.filter(function(e) { return e == entity.sys_id }).length >1) {
						that._addProjectValidationInfo(
							{ 																		
								description: gs.getMessage("Measurment '{0}' used in more then one widget on dashboard '{1}'", [entity.name, dashboard.name]),
								table: "sn_kpi_composer_dashboard",
								record: dashboard.sys_id,
							}, { project: { sys_id: data.project.sys_id } });
					}
				}			

			});
	},

    type: 'ProjectValidationEntityUsedMultipleTimesOnDashboard'
});
