sys_ui_script.script:
var UserListItem = createReactClass({
	getInitialState: function() {
		return { hover: false, delete: false };
	},

	onMouseEnter: function() {
		this.setState({ hover: true });
	},

	onMouseLeave: function() {
		this.setState({ hover: false });
	},

	onDeleteClick: function() {
		this.setState({ delete: true });
	},

	onDeleteNo: function() {
		this.setState({ delete: false });
	},

	onDeleteYes: function() {
		this.props.onDelete(this.props.user.sys_id, function(error){
			//todo: show error
		});
	},

	render: function() {
		var confirmremoval = null;
		var buttons = null;
		var liprops = null;

		if (this.props.onDelete != null) {
			liprops = { onMouseEnter: this.onMouseEnter, onMouseLeave: this.onMouseLeave };
			if (this.state.delete === true) {
				confirmremoval = e(
					ConfirmRemoval,
					{ onYes: this.onDeleteYes, onNo: this.onDeleteNo }
				);
			}

			if (this.state.hover === true) {
				buttons = e(
					'div',
					{ className: 'icon-contain' },
					e(
						'span',
						{ className: 'icon icon-delete sn-tooltip-basic', 'title': getMessage("Delete item"), onClick: this.onDeleteClick}
					)
				);
			}
		}


		return e(
			'li',
			liprops,
			e(
				'div',
				{ className: 'wb-pa-filter-pill-decoration letter'},
				this.props.user.abbreviation
			),
			e(
				'div',
				{ className: 'item-name' },
				this.props.user.name
			),
			buttons,
			confirmremoval
		);
	}
});
