sys_ui_script.script:
var NavHubLibraryTypeTab = createReactClass({
	onClick: function (evt) {
        evt.stopPropagation();
        this.props.onClick(this.props.tab.key);
	},

	render: function() {
        return e(
            'button',
            { className: 'btn btn-icon side-bar-btn' + (this.props.selected ? ' state-active' : ''), role: "tab", "aria-selected" : (this.props.selected ? "true" : "false"), onClick: this.onClick },
            this.props.tab.value
        );
	}
});
