sys_ui_script.script:
var NavHubTitle = createReactClass({
	goBack: function() {
		if (!this.props.returnUrl) {
			g_navigation.open('sn_kpi_composer_projects.do');
		} else {
			g_navigation.open(this.props.returnUrl);
		}
	},

	render: function() {
		var title;
		if (!this.props.returnUrl) {
			title = getMessage('Show all projects');
		} else {
			title = getMessage('Go back');
		}

		var islibraryicon = null;
		if (this.props.project_library) {
			islibraryicon = e(
				'img',
				{src: "wb-bookmark-icon.svg", width: 18, height: 18, style: { marginRight: 5 }}
			);
		}

		return e(
			'div',
			{ className: 'sn-navhub-content' + (this.props.showSeparator ? ' sn-navhub-content_separator' : '') },
			e(
				'div',
				{ className: 'sn-navhub-content sn-navhub-content_static sn-navhub-content_separator'},
				e(
					'button',
					{ className: 'btn btn-icon icon-chevron-left sn-navhub-btn sn-navhub-btn_alt', type: 'button', title: title, 'aria-hidden': 'false', 'aria-label': title, onClick: this.goBack }
				)
			),
			e(
				'div',
				{ className: 'sn-navhub-content sn-navhub-content_row' },
				e(
					'label',
					{ className: 'sn-navhub-title' },
					islibraryicon,
					this.props.title
				)
			)
		);
	}
});
