sys_ui_script.script:
var NavHubCanvasEntityType = createReactClass({
	getInitialState: function() {
		return { open: false };
	},

	toggle: function() {
		this.setState({ open: !this.state.open });
	},

	render: function() {
		var that = this;

		var listitems = this.props.entities.map(
			function (ent) {
				return e(
					NavHubCanvasEntity,
					{ key: ent.sys_id, entity: ent, isused: that.props.selecteddashboardusedentities.indexOf(ent.sys_id) > -1, disableDragging: that.props.disableDragging }
				);
			}
		);

		return e(
			'div',
			{ className: 'navhubcanvasentitytype' },
			e(
				'div',
				{ className: 'typelabel', onClick: this.toggle },
				e(
					'span',
					null,
					this.props.name
				),
				e(
					'span',
					{ className: 'typechevron icon-chevron-' + (this.state.open ? 'down' : 'up') }
				)

			),
			e(
				Reactstrap.Collapse,
				{ isOpen: this.state.open },
				e(
					'div',
					{ className: 'navhubcanvasentitytypes'},
					listitems
				)
			)
		);
	}
});
