sys_ui_script.script:
var wizardtopbuttonsarray = [
	{ id: 'analysis', title: getMessage('Analysis') },
	{ id: 'dashboarddesign', title: getMessage('Dashboard Visualization') },
	{ id: 'indicatordesign', title: getMessage('Data Definition') },
	{ id: 'review', title: getMessage('Review') }
];

var WizardTopButtons = createReactClass({
	change: function(newid) {
		if (this.props.selectedId !== newid) {
			this.props.onWizardStepChange(newid);
		}
	},

	render: function() {
		var that = this;
		return e(
				'ol',
				{ className: "process-breadcrumb process-breadcrumb-border" },
				wizardtopbuttonsarray.filter(function (topbutton) {
					if (that.props.project_library && (topbutton.id === 'dashboarddesign' || topbutton.id === 'review')) return false;
					return true;
				}).map(
					function (btn) { 
						return e(
							'li',
							{ key: btn.id, className: that.props.selectedId === btn.id ? "active" : null },
							e(
								'a',
								{ href: "#", onClick: that.change.bind(that, btn.id) },
								btn.title
							)
						);
					}
				)
			);
	}
});
