sys_ui_script.script:
var FullscreenLoading = createReactClass({
	getDefaultProps: function() {
		return {
			title: getMessage('Loading Project Data...'),
			show: true
		};
	},

	render: function() {
		if (this.props.show === false) return null;

		return e(
			'div',
			{ className: 'wb-pa-loading wb-pa-loading-analysis' },
			e(
				'div',
				{className: 'icon-loading'}
			),
			e(
				'div',
				{className: 'wb-pa-loading-text'},
				this.props.title
			)
		);
	}
});
