sys_ui_script.script:
var IndicatorDefinitionList = createReactClass({
	getInitialState: function() {
		return { add: false };
	},

	addClick: function() {
		this.setState({ add: true, saving: false });
	},

	onAdd: function(indicatordefinition, callback) {
		if (this.props.indicatordefinition == null) {
			this.setState({ saving: true });
			var that = this;
			this.props.onIndicatorDefinitionSave(indicatordefinition, function(error) {
				that.setState({ add: false });
				callback(error);
			});
		} else {
			//er is al een indicator definiton gekoppeld
			callback(null);
		}
	},

	onBlur: function() {
		if (this.state.saving == true) return;

		this.setState({ add: false });
	},

	onDelete: function(sys_id) {
		this.props.onIndicatorDefinitionSave(null, function (error){
			//todo: show error
		});
	},

	handleSubmit: function(event) {
		event.preventDefault();
	},

	render: function() {
		var that = this;

		var toshow = null;
		if (this.props.indicatordefinition != null) {
			toshow =
				e(
				IndicatorDefinitionListItem,
				{ key: this.props.indicatordefinition.sys_id, indicatordefinition: this.props.indicatordefinition, onDelete: (this.props.onIndicatorDefinitionSave ? this.onDelete : null), onIndicatorShowDetail: this.props.onIndicatorShowDetail }
			);
		} else {
			if (this.props.onIndicatorDefinitionSave) {
				if (this.state.add) {
					toshow = e(
						'li',
						{ key: 'addnew', className: 'addnew' },
						e(
							'span',
							{ className: 'icon-add-circle-empty icon'}
						),
						e(
							'form',
							{className: 'item-name', onSubmit: this.handleSubmit},
							e(
								IndicatorDefinitionSelector,
								{ placeholder: this.props.addPlaceholder, disabled: this.state.saving, onIndicatorDefinitionSelected: this.onAdd, onBlur: this.onBlur }
							)
						)
					);
				} else {
					toshow = e(
						'li',
						{ key: 'addnew', className: 'addnew', onClick: this.addClick },
						e(
							'span',
							{ className: 'icon-add-circle-empty icon'}
						),
						e(
							'div',
							{ className: 'item-name' },
							this.props.addPlaceholder
						)
					);
				}
			} else {
				toshow = e(NoneList, { key: 'none' });
			}

		}

		return [
			e(
				'ul',
				{ className: 'sn-widget-datalist', key: 'list' },
				toshow
			)
		];
	}
});
