sys_ui_script.script:
var NavHubButton = createReactClass({
	handleClick : function() {
		this.props.onActiveAsideChange(this.props.id);
	},

	render: function() {
		var className = 'btn btn-icon sn-navhub-btn ' + this.props.iconClass;
		if (this.props.isActive === true) {
			className = className + ' state-active';
		}
		var style = null;
		if (this.props.paddingBottom) {
			style = { paddingBottom: this.props.paddingBottom };
		}
		return e(
			'button',
			{ className: className, type: 'button', 'aria-label': this.props.title, title: this.props.title, 'aria-hidden': 'false', onClick: this.handleClick, style: style },
			this.props.children
		);
	}
});
