sys_ui_script.script:
var ConfirmMessage = createReactClass({
	getDefaultProps: function() {
		return {
			title: getMessage('Confirmation'),
			content: getMessage('Action succeeded')
		};
	},

	getInitialState: function() {
		return { disabled: false };
	},

	ok: function() {
		this.setState({ disabled: true });
		this.props.onOk();
	},

	onOpened: function() {
		if (this.refs.okbutton) {
			this.refs.okbutton.focus();
		}
	},

	render: function() {
		var closeButton = e(
			'button',
			{type: 'button', title: getMessage('Close'), className: 'btn btn-icon close icon-cross  sn-tooltip-basic', disabled: this.state.disabled, onClick: this.ok },
			e(
				'span',
				{ className: 'sr-only' },
				'Close'
			)
		);


		var loadingOverlay = null;
		if (this.state.disabled === true) {
			loadingOverlay = e(
				'div',
				{className: 'modal-overlay'},
				e(
					'div',
					{ className: 'icon-loading' }
				)
			);
		}

		return e(
			Reactstrap.Modal, 
			{'isOpen': true, onOpened: this.onOpened, toggle: this.ok },
			e(
				Reactstrap.ModalHeader,
				{'toggle': this.ok, close: closeButton},
				this.props.title
			),
			e(
				Reactstrap.ModalBody,
				{},
				this.props.content
			),
			e(
				Reactstrap.ModalFooter,
				{},
				e(
					'button',
					{ type: 'button',  ref: 'okbutton', className: 'btn btn-primary', disabled: this.state.disabled, onClick: this.ok },
					getMessage('OK')
				)
			),
			loadingOverlay
		);
	}
});
