sys_ui_script.script:
var UserList = createReactClass({
	getInitialState: function() {
		return { add: false };
	},

	addClick: function() {
		this.setState({ add: true, saving: false });
	},

	onAdd: function(user, callback) {
		if (this.props.users.filter(function (u) { return u.sys_id === user.sys_id; }).length == 0) {
			this.setState({ saving: true });
			var that = this;
			this.props.onUserAdd(user, function(error) {
				that.setState({ add: false });
				callback(error);
			});
		} else {
			//todo: user behoort al tot de lijst, toon bericht
			callback(null);
		}
	},

	/**
	 * When the KnowledgeArticleSelector is hidden on blur
	 * If the user selects something with the mouse click
	 * it would just hide the element and not wait for the async requests
	 * Therefore we need to keep the element there until it actually finishes the selection
	onBlur: function() {
		if (this.state.saving == true) return;

		this.setState({ add: false });
	},
	*/

	onDelete: function(sys_id) {
		this.props.onUserDelete(sys_id, function (error){
			//todo: show error
		});
	},

	handleSubmit: function(event) {
		event.preventDefault();
	},

	render: function() {
		var that = this;

		var userlis = this.props.users.map(
			function (user) {
				return e(
					UserListItem,
					{ key: user.sys_id, user: user, onDelete: that.props.onUserDelete != null ? that.onDelete : null }
				);
			}
		);

		if (this.props.onUserAdd != null) {
			if (this.state.add) {
				userlis.push(
					e(
						'li',
						{ key: 'addnew', className: 'addnew' },
						e(
							'span',
							{ className: 'icon-add-circle-empty icon'}
						),
						e(
							'form',
							{className: 'item-name', onSubmit: this.handleSubmit},
							e(
								UserSelector,
								{ placeholder: this.props.addPlaceholder, disabled: this.state.saving, onUserSelected: this.onAdd }
							)
						)
					)
				);
			} else {
				userlis.push(
					e(
						'li',
						{ key: 'addnew', className: 'addnew', onClick: this.addClick },
						e(
							'span',
							{ className: 'icon-add-circle-empty icon'}
						),
						e(
							'div',
							{ className: 'item-name' },
							this.props.addPlaceholder
						)
					)
				);
			}
		} else {
			if (userlis.length == 0) {
				userlis.push(e(NoneList, { key: 'none' }));
			}
		}

		return [
			e(
				'div',
				{ className: 'sn-aside-group-title', key: 'title' },
				e(
					'span',
					null,
					this.props.title
				)
			),
			e(
				'ul',
				{ className: 'sn-widget-datalist', key: 'list' },
				userlis
			)
		];
	}
});
