sys_ui_script.script:
var ModalPersonas = createReactClass({
	render: function() {
		var that = this;

		var personas = that.props.onCheckedChange != null ? this.props.personas : this.props.personas.filter(function (pers) { return that.props.selectedpersonas.indexOf(pers.sys_id) > -1; });

		var listitems = personas.map(
			function (pers) {
				return e(
					ModalPersonaList,
					{ key: pers.sys_id, persona: pers, checked: that.props.selectedpersonas.indexOf(pers.sys_id) > -1, onCheckedChange: that.props.onCheckedChange }
				);
			}
		);

		if (listitems.length == 0) {
			listitems.push(e(NoneList, { key: 'none' }));
		}

		return e(
			'div',
			null,
			e(
				'div',
				{className: 'sn-aside-header'},
				e(
					'span',
					null,
					getMessage('Persona')
				)
			),
			e(
				'div',
				{className: 'sn-aside-body wb-pa-aside-body'},
				e(
					'ul',
					{className: 'sn-widget-datalist'},
					listitems
				)
			)
		);
	}
});
