sys_ui_script.script:
var ReportDefinitionList = createReactClass({
	getInitialState: function() {
		return { add: false };
	},

	addClick: function() {
		this.setState({ add: true, saving: false });
	},

	onAdd: function(report_definition, callback) {
		if (this.props.report_definition == null) {
			this.setState({ saving: true });
			var that = this;
			this.props.onReportDefinitionSave(report_definition, function(error) {
				that.setState({ add: false });
				callback(error);
			});
		} else {
			//er is al een report definiton gekoppeld
			callback(null);
		}
	},

	onBlur: function() {
		if (this.state.saving == true) return;

		this.setState({ add: false });
	},

	onDelete: function(sys_id) {
		this.props.onReportDefinitionSave(null, function (error){
			//todo: show error
		});
	},

	handleSubmit: function(event) {
		event.preventDefault();
	},

	render: function() {
		var that = this;

		var toshow = null;
		if (this.props.report_definition != null) {
			toshow =
				e(
				ReportDefinitionListItem,
				{ key: this.props.report_definition.sys_id, report_definition: this.props.report_definition, onDelete: (this.props.onReportDefinitionSave ? this.onDelete : null), onReportShowDetail: this.props.onReportShowDetail }
			);
		} else {
			if (this.props.onReportDefinitionSave) {
				if (this.state.add) {
					toshow = e(
						'li',
						{ key: 'addnew', className: 'addnew' },
						e(
							'span',
							{ className: 'icon-add-circle-empty icon'}
						),
						e(
							'form',
							{className: 'item-name', onSubmit: this.handleSubmit},
							e(
								ReportDefinitionSelector,
								{ placeholder: this.props.addPlaceholder, disabled: this.state.saving, onReportDefinitionSelected: this.onAdd, onBlur: this.onBlur }
							)
						)
					);
				} else {
					toshow = e(
						'li',
						{ key: 'addnew', className: 'addnew', onClick: this.addClick },
						e(
							'span',
							{ className: 'icon-add-circle-empty icon'}
						),
						e(
							'div',
							{ className: 'item-name' },
							this.props.addPlaceholder
						)
					);
				}
			} else {
				toshow = e(NoneList, { key: 'none' });
			}
		}

		return [
			e(
				'ul',
				{ className: 'sn-widget-datalist', key: 'list' },
				toshow
			)
		];
	}
});
