sys_ui_script.script:
var ConfirmRemoval = createReactClass({
	getDefaultProps: function() {
		return {
			title: getMessage('Confirm removal'),
			content: getMessage('Are you sure you wish to delete this?'),
			yesLabel: getMessage('Yes'),
			noLabel: getMessage('No')
		};
	},

	getInitialState: function() {
		return { disabled: false };
	},

	no: function() {
		this.setState({ disabled: true });
		this.props.onNo();
	},

	yes: function() {
		this.setState({ disabled: true });
		this.props.onYes();
	},

	onOpened: function() {
		if (this.refs.yesbutton) {
			this.refs.yesbutton.focus();
		}
	},

	render: function() {
		var closeButton = e(
			'button',
			{type: 'button', title: getMessage('Close'), className: 'btn btn-icon close icon-cross  sn-tooltip-basic', disabled: this.state.disabled, onClick: this.no },
			e(
				'span',
				{ className: 'sr-only' },
				'Close'
			)
		);


		var loadingOverlay = null;
		if (this.state.disabled === true) {
			loadingOverlay = e(
				'div',
				{className: 'modal-overlay'},
				e(
					'div',
					{ className: 'icon-loading' }
				)
			);
		}

		return e(
			Reactstrap.Modal, 
			{'isOpen': true, onOpened: this.onOpened, toggle: this.no },
			e(
				Reactstrap.ModalHeader,
				{'toggle': this.no, close: closeButton},
				this.props.title
			),
			e(
				Reactstrap.ModalBody,
				{},
				this.props.content
			),
			e(
				Reactstrap.ModalFooter,
				{},
				e(
					'button',
					{ type: 'button', className: 'btn btn-default', disabled: this.state.disabled, onClick: this.no },
					this.props.noLabel
				),
				e(
					'button',
					{ type: 'button',  ref: 'yesbutton', className: 'btn btn-destructive', disabled: this.state.disabled, onClick: this.yes },
					this.props.yesLabel
				)
			),
			loadingOverlay
		);
	}
});
