sys_ui_script.script:
var EntityInfo = createReactClass({
	render: function() {
		return e(
			'div',
			null,
			e(
				'div',
				{className: 'sn-aside-header'},
				e(
					'span',
					null,
					getMessage('Info')
				)
			),
			e(
				'div',
				{className: 'sn-aside-body wb-pa-aside-body'},
				e(
					KnowledgeArticleList,
					{
						knowledgearticles: this.props.entity.knowledgearticles,
						title: getMessage('Knowledge Articles'),
						addPlaceholder: getMessage('Add Item'),
						onKnowledgeArticleAdd: this.props.onKnowledgeArticleAdd,
						onKnowledgeArticleDelete: this.props.onKnowledgeArticleDelete
					}
				),
				e(
					UserList,
					{
						users: this.props.entity.owners,
						title: getMessage('Responsible'),
						addPlaceholder: getMessage('Add Responsible'),
						onUserAdd: this.props.onOwnerAdd,
						onUserDelete: this.props.onOwnerDelete
					}
				),
				e(
					UserList,
					{
						users: this.props.entity.contacts,
						title: getMessage('Contacts'),
						addPlaceholder: getMessage('Add Contact'),
						onUserAdd: this.props.onContactAdd,
						onUserDelete: this.props.onContactDelete
					}
				)
			)
		);
	}
});
