sys_ui_script.script:
var ModalBreakdownLi = createReactClass({
	getInitialState: function() {
		return { checked: this.props.checked };
	},

	saveChecked: function(event) {
		this.setState({ saving_checked: true, checked: event.target.checked });
		var that = this;
		this.props.onCheckedChange(this.props.breakdown.sys_id, event.target.checked, function(error) {
			that.setState({ saving_checked: false });
		});
	},

	render: function() {
		var inputswitch = null;
		if (this.props.onCheckedChange != null) {
			inputswitch = e(
				'span',
				{ className: 'input-switch' },
				e(
					'input',
					{ 'aria-label': getMessage('Toggle breakdown'),id: 'edit_checked_' + this.props.breakdown.sys_id, type: 'checkbox', name: 'edit_checked_' + this.props.breakdown.sys_id, disabled: this.state.saving_checked, checked: this.state.checked, onChange: this.saveChecked }
				),
				e(
					'label',
					{ className: 'switch', 'aria-hidden': true, htmlFor: 'edit_checked_' + this.props.breakdown.sys_id }
				)
			);
		}

		return e(
			'li',
			{ },
			e(
				'div',
				{ className: 'wb-pa-filter-pill-decoration', style: { backgroundColor: this.props.breakdown.color }}
			),
			e(
				'div',
				{ className: 'item-name' },
				this.props.breakdown.name
			),
			inputswitch
		);
	}
});
