sys_ui_script.script:
var DashboardPaDashboard = createReactClass({
	render: function() {
		var pa_dashboard = null;
		if (this.props.dashboard.pa_dashboard != null) {
			var dashboard = this.props.dashboard;
			var pa_dashboards = this.props.pa_dashboards.filter(function (d) { return d.sys_id === dashboard.pa_dashboard; });
			if (pa_dashboards.length == 1) {
				pa_dashboard = pa_dashboards[0];
			}
		}

		return e(
			'div',
			null,
			e(
				'div',
				{className: 'sn-aside-header'},
				e(
					'span',
					null,
					'PA Dashboard'
				)
			),
			e(
				'div',
				{className: 'sn-aside-body wb-pa-aside-body'},
				e(
					PaDashboardList,
					{
						pa_dashboard: pa_dashboard,
						addPlaceholder: getMessage('Select Item'),
						onPaDashboardSave: this.props.onPaDashboardSave
					}
				)
			)
		);
	}
});
