sys_ui_script.script:
var OverviewTabsAndFilter = createReactClass({
	getInitialState: function() {
		return {
			filter_text: this.props.filter_text
		};
	},

	handleFilterTextChange: function(event) {
		this.setState({ filter_text: event.target.value });
	},

	handleFilterTextBlur: function(event) {
		this.handleFilterTextChanged();
	},

	handleFilterTextSubmit: function(event) {
		event.preventDefault();
		this.handleFilterTextChanged();
	},

	handleFilterTextChanged: function() {
		this.props.onFilterTextChange(this.state.filter_text);
	},

	componentDidUpdate: function (prevProps) {
		if (this.props.filter_text !== prevProps.filter_text && this.props.filter_text !== this.state.filter_text) {
			this.setState({ filter_text: this.props.filter_text });
		}
	},

	render: function() {
		var that = this;
		return e(
				'div',
				{ className: "row pa-overview-tabs-and-filters" },
				e(
					'div',
					{ className: 'pa-overview-tabs', "arrow-keys-accessibility": 'leftRight', role: 'tablist' },
					ALLTABS.map(function (tab) {
						return e(
							OverviewTab,
							{ key: tab.id, selected: that.props.currentTab === tab.id, tab: tab, onTabChanged: that.props.onTabChanged }
						);
					})

				),
				e(
					'div',
					{ className: "pa-overview-filters-container", role: 'search' },
					e(
						'div',
						{ className: "pa-overview-search-box-container" },
						e(
							'div',
							{ className: "pa-overview-search-box icon-search", role: 'search' },
							e(
								'form',
								{ onSubmit: this.handleFilterTextSubmit },
								e(
									'input',
									{
										className: "pa-overview-search-input",
										type: "search",
										"aria-label": getMessage("Search projects overview"),
										placeholder: getMessage("Search by name"),
										value: this.state.filter_text,
										onChange: this.handleFilterTextChange, onBlur: this.handleFilterTextBlur
									}
								)
							),
							e(
								'button',
								{ className: "btn btn-icon icon-cross-circle clear-search-container sn-tooltip-basic", title: getMessage("Clear"), "aria-label": getMessage("Clear search"), onClick: this.props.onClearFilter}
							)
						)
					)
				)
			);
	}
});
