sys_ui_script.script:
var ReviewBadgeItem = createReactClass({
	getDefaultProps: function() {
		return {
			badgecolor: 'badge-grey'
		};
	},

	render: function() {
		return e(
			'div',
			{ className: 'sn-card-component sn-card-component_meta extra-padding-lg' },
			e(
				'a',
				{ className: 'sn-wb-pa-cursor', href: this.props.link, target: this.props.linktarget },
				e(
					'span',
					{ className: ('badge ' + this.props.badgecolor)},
					this.props.number
				),
				e(
					'span',
					{ className: 'sn-wb-pa-badge-label'},
					this.props.label
				)
			)
		);
	}
});
