sys_ui_script.script:
var NavHubContainer = createReactClass({
	render: function() {
		var activeAsideClassName = '';
		var activeAsideComponent = null;

		if (this.props.activeAside == null) {
			activeAsideClassName = ' sn-aside-hide';
		} else {
			if (this.props.activeAside === 'info') {
				activeAsideComponent =
					e(
					NavHubInfo,
					this.props
				);
			} else if (this.props.activeAside === 'journal') {
				activeAsideComponent =
					e(
					NavHubJournal,
					this.props
				);
			} else if (this.props.activeAside === 'library') {
				activeAsideComponent =
					e(
					NavHubLibrary,
					this.props
				);
			} else if (this.props.activeAside === 'share') {
				activeAsideComponent =
					e(
					NavHubShareContainer,
					this.props
				);
			} else if (this.props.activeAside === 'personas') {
				activeAsideComponent =
					e(
					NavHubPersonas,
					this.props
				);
			} else if (this.props.activeAside === 'breakdowns') {
				activeAsideComponent =
					e(
					NavHubBreakdowns,
					this.props
				);
			} else if (this.props.activeAside === 'addwidget') {
				activeAsideComponent =
					e(
					NavHubAddWidget,
					this.props
				);
			} else if (this.props.activeAside === 'canvasentities') {
				activeAsideComponent =
					e(
					NavHubCanvasEntities,
					this.props
				);
			}
		}

		return e(
					'div',
					{className: 'sn-aside sn-aside_right sn-aside_header sn-connect-aside right am-fade-and-slide-right sn-aside_open' + activeAsideClassName, style: { width: '320px', display: 'block' }}, 
					e(
						'div',
						{className: 'aside-dialog', style: {minWidth: '320px'}},
						e(
							'div',
							{className: 'aside-content'},
							e(
								'div',
								null,
								activeAsideComponent
							)
						)
					)
				);
	}
});
