sys_ui_script.script:
var NavHubAddWidgetItem = createReactClass({
	onDragStart: function(evt) {
		var value = { category: "visualisation", sys_id: this.props.widgetvisualisation.sys_id };

		evt.dataTransfer.effectAllowed = 'copyMove';
		/**
		 * DataTransfer.setData only supports "Text" in AI (A.K.A. text/plain) as type
		 */
		evt.dataTransfer.setData("Text", JSON.stringify(value));
		//console.log('SET GET', evt, evt.dataTransfer.getData('Text'))
	},

	render: function() {
		var divprops = { className: 'navhubvisualisation' };
		if (this.props.disableDragging !== true) {
			divprops.className += " draggable";
			divprops.draggable = true;
			divprops.onDragStart = this.onDragStart;
		}

		return e(
			'div',
			divprops,
			this.props.widgetvisualisation.name
		);
	}
});
